import fetch from 'node-fetch';

export default async function batchUpdateGoogleDocument({
    documentUrl,
    requests,
}: NodeInputs, {
    auth
}: NodeScriptOptions) : NodeOutput  {
    const { access_token } = await auth.getToken();

    // Extracting spreadsheetId from the URL
    const regex = /\/document\/d\/([a-zA-Z0-9-_]+)/;
    const match = documentUrl.match(regex);
    if (!match) {
        throw new Error('Invalid spreadsheet URL');
    }
    const documentId = match[1];

    const requestBody = {
        requests: requests,
    };

    const response = await fetch(`https://docs.googleapis.com/v1/documents/${documentId}:batchUpdate`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${access_token}`
        },
        body: JSON.stringify(requestBody)
    });

    return response.json();
}

import fetch from 'node-fetch';
export default async function createGoogleDoc({
    title
}: NodeInputs, {
  auth
}: NodeScriptOptions) : NodeOutput  {
    const { access_token } = await auth.getToken();
    const response = await fetch('https://docs.googleapis.com/v1/documents', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${access_token}`
        },
        body: JSON.stringify({
            title
        })
    });
    const data = await response.json();
    return data;
}

