import fetch from 'node-fetch';
export default async function sendDocumentForSignature({
    bearer_token,
    document_id,
    subject,
    message,
    from,
    to
}: NodeInputs) : NodeOutput  {
    const response = await fetch('https://api-eval.signnow.com/document/' + document_id + '/invite', {
        method: 'POST',
        headers: {
            'Authorization': 'Bearer ' + bearer_token,
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        },
        body: JSON.stringify({
            document_id,
            subject,
            message,
            from,
            to
        })
    });
    return response.json();
}
