import Typesense from 'typesense';

export default async function typesenseSearch({
    host,
    apiKey,
    collectionName,
    query,
    field,
    filterBy = '',
    sortBy = '',
}: NodeInputs) : NodeOutput  {
  try {  
    const client = new Typesense.Client({
          'nodes': [{
              'host': host,
              'port': 443,
              'protocol': 'https'
          }],
          'apiKey': apiKey
      });

      let searchParameters = {
          'q': query,
          'query_by': field,
          'filter_by': filterBy,
          'sort_by': sortBy,
          'page': 1,
          'per_page': 10
      };

        const searchResults = await client.collections(collectionName).documents().search(searchParameters);
        return searchResults.hits;
    } catch (e) {
        console.error('Search error:', e);
        return null;
    }
}

import Typesense from 'typesense';

export default async function typesenseGeoSearch({
    host,
    apiKey,
    collectionName,
    searchQuery = '*',
    location,
    radius,
    sortBy = ''
}: NodeInputs) : NodeOutput  {
    const client = new Typesense.Client({
        'nodes': [{
            'host': host,
            'port': 443,
            'protocol': 'https'
        }],
        'apiKey': apiKey
    });

    let searchParameters = {
        'q': searchQuery,
        'query_by': 'title',
        'filter_by': `location:(${location}, ${radius} km)`,
        'sort_by': sortBy,
        'page': 1,
        'per_page': 10,
    };

    try {
        const searchResults = await client.collections(collectionName)
            .documents()
            .search(searchParameters);
        return searchResults.hits;
    } catch (e) {
        console.error('GeoSearch error:', e);
        return null;
    }
}

import Typesense from 'typesense';

export default async function typesenseVectorSearch({
    host,
    apiKey,
    collectionName,
    searchQuery = '*',
    vectorField,
    vector,
    k = 10, // Number of nearest neighbors to find
    sortBy = ''
}: NodeInputs) : NodeOutput  {
    const client = new Typesense.Client({
        'nodes': [{
            'host': host,
            'port': 443,
            'protocol': 'https'
        }],
        'apiKey': apiKey
    });

    let vectorQuery = `${vectorField}:(${vector.join(',')}, k:${k})`;

    let searchParameters = {
        'q': searchQuery,
        'query_by': vectorField,
        'vector_query': vectorQuery,
        'sort_by': sortBy,
        'page': 1,
        'per_page': 10,
    };

    try {
        const searchResults = await client.collections(collectionName)
            .documents()
            .search(searchParameters);
        return searchResults.hits;
    } catch (e) {
        console.error('VectorSearch error:', e);
        return null;
    }
}

